pages/cart:
---
cart: true
---
{{#partial "page"}}
<div class="page">
    <div class="container">
        <main class="page-content" data-cart>
            {{> components/common/breadcrumbs breadcrumbs=breadcrumbs}}

            {{> components/cart/page-title}}

            <div data-cart-status>
                {{> components/cart/status-messages}}
            </div>

            {{#if cart.items.length}}
                <div class="loadingOverlay"></div>

                <div data-cart-content>
                    {{> components/cart/content}}
                </div>

                <div data-cart-totals>
                    {{> components/cart/totals}}
                </div>

                {{#if cart.show_primary_checkout_button}}
                    <div class="cart-actions">
                        <a class="button button--primary" href="{{urls.checkout.single_address}}" title="{{lang 'cart.checkout.title'}}">{{lang 'cart.checkout.button'}}</a>
                        {{#if cart.show_multiple_address_shipping}}
                            <a class="checkoutMultiple" href="{{urls.checkout.multiple_address}}">
                                {{lang 'cart.preview.checkout_multiple'}}
                            </a>
                        {{/if}}
                    </div>
                {{else}}
                    <div class="cart-actions">
                        <a class="button" href="{{urls.home}}" title="{{lang 'cart.continue_shopping'}}">{{lang 'cart.continue_shopping'}}</a>
                    </div>
                {{/if}}

                {{#if cart.additional_checkout_buttons}}
                    <div class="cart-additionalCheckoutButtons">
                        {{#each cart.additional_checkout_buttons}}
                            {{{this}}}
                        {{/each}}
                    </div>
                {{/if}}
            {{else}}
                <h3>{{lang 'cart.checkout.empty_cart'}}</h3>
            {{/if}}
        </main>
    </div>
</div>
{{/partial}}
{{> layout/base}}

components/cart/item-giftwrap:
{{#if show_gift_wrapping}}
    <dl class="cart-item-options cart-item-options--giftWrapping">
        <dt class="cart-item-option-title">
            {{lang 'cart.gift_wrapping.title'}}:
        </dt>
        {{#if gift_wrapping}}
            <dd class="cart-item-option-description">
                {{gift_wrapping.name}}
                <span class="cart-item-option-actions">
                    (<a href="" data-item-giftwrap="{{id}}">{{lang 'cart.gift_wrapping.change'}}</a> or <a href="{{gift_wrapping.remove_url}}" onclick="return confirm('{{lang 'cart.gift_wrapping.remove_confirm'}}')">{{lang 'cart.gift_wrapping.remove'}}</a>)
                </span>
            </dd>
            {{#if gift_wrapping.message}}
                <dt class="cart-item-option-title">
                    {{lang 'cart.gift_wrapping.gift_message'}}:
                </dt>
                <dd class="cart-item-option-description">
                    {{gift_wrapping.message}}
                </dd>
            {{/if}}
        {{else}}
            <dd class="cart-item-option-description">
                <a href="" data-item-giftwrap="{{id}}">{{lang 'cart.gift_wrapping.add'}}</a>
            </dd>
        {{/if}}
    </dl>
{{/if}}

components/common/payment-icons:
{{#with theme_settings}}
    {{#or show_accept_amex show_accept_discover show_accept_mastercard show_accept_paypal show_accept_visa show_accept_amazonpay show_accept_googlepay show_accept_klarna}}
    <div class="footer-payment-icons">
        {{#if show_accept_amex}}
        <svg class="footer-payment-icon"><use xlink:href="#icon-logo-american-express"></use></svg>
        {{/if}}
        {{#if show_accept_discover}}
        <svg class="footer-payment-icon"><use xlink:href="#icon-logo-discover"></use></svg>
        {{/if}}
        {{#if show_accept_mastercard}}
        <svg class="footer-payment-icon"><use xlink:href="#icon-logo-mastercard"></use></svg>
        {{/if}}
        {{#if show_accept_paypal}}
        <svg class="footer-payment-icon"><use xlink:href="#icon-logo-paypal"></use></svg>
        {{/if}}
        {{#if show_accept_visa}}
        <svg class="footer-payment-icon"><use xlink:href="#icon-logo-visa"></use></svg>
        {{/if}}
        {{#if show_accept_amazonpay}}
        <svg class="footer-payment-icon"><use xlink:href="#icon-logo-amazonpay"></use></svg>
        {{/if}}
        {{#if show_accept_googlepay}}
        <svg class="footer-payment-icon"><use xlink:href="#icon-logo-googlepay"></use></svg>
        {{/if}}
        {{#if show_accept_applepay}}
        <svg class="footer-payment-icon footer-payment-icon-apple"><use xlink:href="#icon-logo-apple-pay"></use></svg>
        {{/if}}
        {{#if show_accept_klarna}}
        <svg class="footer-payment-icon"><use xlink:href="#icon-logo-klarna"></use></svg>
        {{/if}}
    </div>
    {{/or}}
{{/with}}

components/common/navigation-list:
{{#if children}}
<a class="navPages-action has-subMenu{{#if is_active}} activePage{{/if}}" href="{{url}}" data-collapsible="navPages-{{id}}">
    {{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
</a>
<div class="column-menu navPage-subMenu" id="navPages-{{id}}" aria-hidden="true" tabindex="-1">
    <ul class="navPage-subMenu-list">
        {{#each children}}
            <li class="navPage-subMenu-item">
                <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-right" /></svg></i>
                <a class="navPage-subMenu-action navPages-action{{#if is_active}} activePage{{/if}}" href="{{url}}">{{name}}</a>
                {{#if children}}
                    <i class="icon navPages-action-moreIcon navPages-action-closed" aria-hidden="true"><svg><use xlink:href="#icon-plus" /></svg></i>
                    <i class="icon navPages-action-moreIcon navPages-action-open" aria-hidden="true"><svg><use xlink:href="#icon-minus" /></svg></i>
                {{/if}}

            </li>
            {{#if children}}
                {{#each children}}
                    <li class="navPage-childList-item">
                        <a class="navPage-childList-action navPages-action{{#if is_active}} activePage{{/if}}" href="{{url}}">{{name}}</a>
                    </li>
                {{/each}}
            {{/if}}
        {{/each}}
    </ul>
</div>
{{else}}
<a class="navPages-action{{#if is_active}} activePage{{/if}}" href="{{url}}">{{name}}</a>
{{/if}}

components/common/navigation-list-altern:
{{#if children}}
    <a class="navPages-action navPages-action-depth-max has-subMenu is-root{{#if is_active}} activePage{{/if}}" href="{{url}}" data-collapsible="navPages-{{id}}">
        {{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
    </a>
    {{> components/common/navigation-dropdown}}
{{else}}
    <a class="navPages-action{{#if is_active}} activePage{{/if}}" href="{{url}}">{{name}}</a>
{{/if}}

components/common/social-links:
{{#if social_media}}
    <ul class="socialLinks socialLinks--alt">
        {{#each social_media}}
            <li class="socialLinks-item">
                <a class="icon icon--{{display_name}}" href="{{url}}" target="_blank" rel="noopener">
                    <svg><use xlink:href="#icon-{{display_name}}" /></svg>
                </a>
            </li>
        {{/each}}
    </ul>
{{/if}}
